import React, { useEffect, useState } from "react";
import "./EndowList.css";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

function EndowList() {
  const [endows, setEndows] = useState([]);
  const [index, setIndex] = useState(0);
  const itemsPerPage = 3;

  // fetch data (giữ nguyên)
  useEffect(() => {
    fetch("http://localhost:5000/endow")
      .then((res) => {
        if (!res.ok) throw new Error("Network error");
        return res.json();
      })
      .then((data) => setEndows(data || []))
      .catch((err) => console.error("Lỗi fetch endows:", err));
  }, []);

  // navigation - Đã cập nhật để chỉ còn logic vòng lặp
  const next = () => {
    // Vòng lặp: Nếu index = endows.length - 1, (index + 1) % endows.length = 0
    setIndex((prev) => (prev + 1) % endows.length);
  };
  const prev = () => {
    // Vòng lặp: Nếu index = 0, (0 - 1 + length) % length = length - 1
    setIndex((prev) => (prev - 1 + endows.length) % endows.length);
  };

  // tính phần trăm dịch chuyển (giữ nguyên)
  const offsetPercent = (100 / itemsPerPage) * index;

  return (
    <section className="endow-section">
      <div className="endow-container">
        <div className="endow-title">
          Ưu đãi
          <br />
          đặc biệt
        </div>

        <div className="endow-slider-area">
          {/* Nút prev chỉ hiển thị nếu có nhiều hơn số itemsPerPage */}
          {endows.length > itemsPerPage && (
            <button className="endow-arrow left" onClick={prev}>
              <FaChevronLeft />
            </button>
          )}

          <div className="endow-viewport">
            <div
              className="endow-track"
              style={{
                transform: `translateX(-${offsetPercent}%)`,
                transition: "transform 0.45s ease",
              }}
            >
              {endows.map((item) => (
                <article
                  key={item.ID}
                  className="endow-card"
                  onClick={() => (window.location.href = `/endow/${item.ID}`)}
                >
                  <img
                    className="endow-poster"
                    alt={item.name}
                    src={require(`../../assets/images/Endow/${item.ID}.jpg`)}
                    onError={(e) => {
                      try {
                      } catch {
                        e.target.src = "";
                      }
                    }}
                  />
                  <h3 className="endow-name">{item.name}</h3>
                </article>
              ))}
            </div>
          </div>

          {/* Nút next chỉ hiển thị nếu có nhiều hơn số itemsPerPage */}
          {endows.length > itemsPerPage && (
            <button className="endow-arrow right" onClick={next}>
              <FaChevronRight />
            </button>
          )}
        </div>
      </div>
    </section>
  );
}

export default EndowList;
