import React, { useEffect, useState, useRef } from "react";
import "./MovieList.css";
import {
  FaPlay,
  FaTicketAlt,
  FaChevronLeft,
  FaChevronRight,
  FaTimes,
} from "react-icons/fa";

function MovieList() {
  const [films, setFilms] = useState([]);
  const [pageIndex, setPageIndex] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [modalUrl, setModalUrl] = useState("");
  const containerRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:5000/films")
      .then((res) => res.json())
      .then((data) => setFilms(data))
      .catch((err) => console.error("Lỗi fetch films:", err));
  }, []);

  const itemsPerPage = 5;
  const totalPages = Math.max(1, Math.ceil(films.length / itemsPerPage));

  // Next / Prev
  const prevPage = () => setPageIndex((p) => (p > 0 ? p - 1 : totalPages - 1));
  const nextPage = () => setPageIndex((p) => (p < totalPages - 1 ? p + 1 : 0));

  // Open modal (embedding youtube/vimeo links or full url)
  const openModalWith = (link) => {
    // if it's a youtube link, we keep as is for iframe src
    setModalUrl(link);
    setShowModal(true);
  };
  const closeModal = () => {
    setShowModal(false);
    setModalUrl("");
  };

  // Keyboard escape close
  useEffect(() => {
    const onKey = (e) => {
      if (e.key === "Escape") closeModal();
    };
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, []);

  // Prepare pages: array of pages, each page is array of films
  const pages = [];
  for (let i = 0; i < totalPages; i++) {
    pages.push(films.slice(i * itemsPerPage, i * itemsPerPage + itemsPerPage));
  }

  return (
    <section className="movie-list-container" ref={containerRef}>
      <h2 className="movie-list-title">
        <span className="movie-list-title-inner">🎬 PHIM ĐANG CHIẾU</span>
      </h2>

      {/* arrows (absolute) */}
      <button
        className="arrow left"
        onClick={prevPage}
        aria-label="previous"
        title="Previous"
      >
        <FaChevronLeft />
      </button>
      <button
        className="arrow right"
        onClick={nextPage}
        aria-label="next"
        title="Next"
      >
        <FaChevronRight />
      </button>

      <div className="movie-slider-viewport">
        <div
          className="movie-track"
          style={{
            width: `${totalPages * 100}%`,
            transform: `translateX(-${(pageIndex * 100) / totalPages}%)`,
          }}
        >
          {pages.map((pageFilms, pIdx) => (
            <div className="movie-page" key={`page-${pIdx}`}>
              {pageFilms.map((film) => (
                <article key={film.ID} className="movie-card">
                  <div
                    className="poster-wrapper"
                    onClick={() => (window.location.href = `/film/${film.ID}`)}
                  >
                    <img
                      className="movie-poster"
                      alt={film.name}
                      // keep dynamic require as your project used before
                      src={require(`../../assets/images/Film/${film.ID}.jpg`)}
                    />

                    {/* dark overlay on hover */}
                    <div className="card-overlay" />

                    <div className="overlay-buttons">
                      {film.link && (
                        <button
                          className="btn play"
                          onClick={(e) => {
                            e.stopPropagation();
                            openModalWith(film.link);
                          }}
                          aria-label="Play trailer"
                          title="Play trailer"
                        >
                          <FaPlay />
                        </button>
                      )}

                      <button
                        className="btn buy"
                        onClick={(e) => {
                          e.stopPropagation();
                          // mua vé action - redirect or open modal, keep simple redirect
                          window.location.href = `/film/${film.ID}/buy`;
                        }}
                      >
                        <FaTicketAlt />
                        <span className="buy-text">MUA VÉ NGAY</span>
                      </button>
                    </div>
                  </div>

                  <h3 className="movie-name" title={film.name}>
                    {film.name}
                  </h3>
                  <p className="movie-type">Thể loại: {film.type}</p>
                </article>
              ))}

              {/* Fill empty slots so layout spacing consistent (for last page) */}
              {pageFilms.length < itemsPerPage &&
                Array.from({ length: itemsPerPage - pageFilms.length }).map(
                  (_, i) => (
                    <div
                      className="movie-card movie-card--empty"
                      key={`e-${i}`}
                    />
                  )
                )}
            </div>
          ))}
        </div>
      </div>

      {/* dots */}
      <div className="carousel-dots">
        {Array.from({ length: totalPages }).map((_, idx) => (
          <button
            key={idx}
            className={`dot ${idx === pageIndex ? "active" : ""}`}
            onClick={() => setPageIndex(idx)}
            aria-label={`Go to page ${idx + 1}`}
          />
        ))}
      </div>

      {/* Modal */}
      {showModal && (
        <div
          className="video-modal-overlay"
          onMouseDown={(e) => {
            // click outside modal content closes
            if (e.target.classList.contains("video-modal-overlay"))
              closeModal();
          }}
        >
          <div className="video-modal">
            <button
              className="modal-close"
              onClick={closeModal}
              aria-label="Close"
            >
              <FaTimes />
            </button>

            {/* If youtube link, convert to embed url */}
            <div className="video-wrapper">
              {modalUrl.includes("youtube.com") ||
              modalUrl.includes("youtu.be") ? (
                <iframe
                  title="Trailer"
                  src={convertYoutubeToEmbed(modalUrl)}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                />
              ) : (
                // fallback: open url inside iframe directly
                <iframe
                  title="Trailer"
                  src={modalUrl}
                  frameBorder="0"
                  allowFullScreen
                />
              )}
            </div>
          </div>
        </div>
      )}
    </section>
  );
}

// small helper to convert youtube watch url into embed url with autoplay=1 optional
function convertYoutubeToEmbed(url) {
  try {
    const u = new URL(url);
    let id = "";
    if (u.hostname.includes("youtu.be")) {
      id = u.pathname.slice(1);
    } else {
      id = u.searchParams.get("v");
    }
    if (!id) return url;
    return `https://www.youtube.com/embed/${id}?rel=0&autoplay=1`;
  } catch (err) {
    return url;
  }
}

export default MovieList;
