import React, { useState, useEffect } from "react";
import { Carousel } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./MovieSlider.css";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

import Slider1 from "../../assets/images/Slider/Slider1.jpg";
import Slider2 from "../../assets/images/Slider/Slider2.jpg";
import Slider3 from "../../assets/images/Slider/Slider3.jpg";
import Slider4 from "../../assets/images/Slider/Slider4.jpg";
import Slider5 from "../../assets/images/Slider/Slider5.jpg";

function MovieSlider() {
  const sliders = [
    { img: Slider1, title: "T·ª¨ CHI·∫æN TR√äN KH√îNG" },
    { img: Slider2, title: "CH·ªä NG√É EM N√ÇNG" },
    { img: Slider3, title: "TAY ANH GI·ªÆ M·ªòT V√å SAO" },
    { img: Slider4, title: "MEASURE IN LOVE: NƒÇM C·ª¶A ANH, NG√ÄY C·ª¶A EM" },
    { img: Slider5, title: "CHAINSAW MAN: THE MOVIE - REZE ARC" },
  ];

  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      handleNext();
    }, 30000);
    return () => clearInterval(interval);
  }, []);

  const handleSelect = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  const handlePrev = () => {
    setIndex((prev) => (prev === 0 ? sliders.length - 1 : prev - 1));
  };

  const handleNext = () => {
    setIndex((prev) => (prev + 1) % sliders.length);
  };

  document.addEventListener("DOMContentLoaded", () => {
    const slider = document.getElementById(".movie-slider");
    console.log(slider);
    if (!slider) return;

    // ƒê·ªãnh nghƒ©a kho·∫£ng ng·∫´u nhi√™n (Gi·ªõi h·∫°n c·ªßa b·∫°n)
    // Lu·ªìng Tr√°i: H·ªôi t·ª• t·ª´ 55% ƒë·∫øn 75% (ph·∫£i ƒëi qua t√¢m)
    const RANGE_LEFT_MIN = 55;
    const RANGE_LEFT_MAX = 75;

    // Lu·ªìng Ph·∫£i: H·ªôi t·ª• t·ª´ 25% ƒë·∫øn 45% (ph·∫£i ƒëi qua t√¢m)
    const RANGE_RIGHT_MIN = 25;
    const RANGE_RIGHT_MAX = 45;

    // H√†m t·∫°o s·ªë ng·∫´u nhi√™n trong kho·∫£ng [min, max]
    function getRandomInt(min, max) {
      return Math.floor(Math.random() * (max - min + 1)) + min;
    }

    // H√†m t√≠nh to√°n v√† √°p d·ª•ng ƒëi·ªÉm h·ªôi t·ª• m·ªõi
    function updateRandomEndPoints() {
      // 1. T√≠nh to√°n ng·∫´u nhi√™n
      const newEndL = getRandomInt(RANGE_LEFT_MIN, RANGE_LEFT_MAX);
      const newEndR = getRandomInt(RANGE_RIGHT_MIN, RANGE_RIGHT_MAX);

      // 2. √Åp d·ª•ng gi√° tr·ªã m·ªõi (ph·∫£i l√† chu·ªói 'xx%')
      slider.style.setProperty("--end-L", `${newEndL}%`);
      slider.style.setProperty("--end-R", `${newEndR}%`);

      console.log(`New End Points: Left=${newEndL}%, Right=${newEndR}%`);
    }

    // L·∫Øng nghe s·ª± ki·ªán k·∫øt th√∫c animation c·ªßa m·ªôt trong hai pseudo-element
    // L·∫Øng nghe ::after v√¨ n√≥ c√≥ th·ªùi gian ng·∫Øn h∆°n (3s)
    slider.addEventListener("animationiteration", (e) => {
      // Ch·ªâ ch·∫°y khi animation 'spotlight-right' l·∫∑p l·∫°i
      if (e.animationName === "spotlight-right") {
        updateRandomEndPoints();
      }
    });

    // Kh·ªüi t·∫°o l·∫ßn ƒë·∫ßu ti√™n ƒë·ªÉ ƒëi·ªÉm ƒë·∫ßu ti√™n c≈©ng ng·∫´u nhi√™n
    updateRandomEndPoints();
  });

  return (
    <div className="movie-slider">
      <Carousel
        activeIndex={index}
        onSelect={handleSelect}
        interval={null}
        controls={false}
        indicators={false}
        fade
      >
        {sliders.map((slide, i) => (
          <Carousel.Item key={i}>
            <div className="slider-img-wrapper">
              <img
                src={slide.img}
                alt={`Slide ${i + 1}`}
                className="d-block w-100 slider-img"
              />
              <div className="overlay"></div>

              <div className="slider-content">
                <h2 className="movie-title">{slide.title}</h2>
                <div className="btn-group">
                  <button className="btn btn-success me-2 slider-btn-buy">
                    üéü MUA V√â NGAY
                  </button>
                  <button className="btn btn-outline-light slider-btn-infor">
                    TH√îNG TIN CHI TI·∫æT ‚Ñπ
                  </button>
                </div>
              </div>
            </div>
          </Carousel.Item>
        ))}
      </Carousel>

      {/* N√∫t ƒëi·ªÅu h∆∞·ªõng Prev / Next */}
      <button className="nav-btn prev" onClick={handlePrev}>
        <FaChevronLeft />
      </button>
      <button className="nav-btn next" onClick={handleNext}>
        <FaChevronRight />
      </button>

      {/* Thumbnail ch·ªçn slide */}
      <div className="thumbnail-wrapper">
        {sliders.map((slide, i) => (
          <img
            key={i}
            src={slide.img}
            alt={`thumb-${i}`}
            className={`thumbnail ${i === index ? "active" : ""}`}
            onClick={() => setIndex(i)}
          />
        ))}
      </div>
    </div>
  );
}

export default MovieSlider;
