import React, { useEffect, useState, useRef } from "react";
import "./EndowList.css";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

/**
 * EndowList - carousel that always shows 3 equal cards visible at once.
 * - Cards sized via CSS: flex-basis: calc((100% - 2*gap)/3)
 * - JS measures real card width (px) to compute `step = cardWidth + gap` for pixel-perfect translate.
 * - Infinite loop implemented with 3x extended array and index normalization on transition end.
 * - No inline width on cards (CSS controls width), track transform uses measured step.
 */

function EndowList() {
  const [endows, setEndows] = useState([]);
  const [offsetIndex, setOffsetIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(true);

  const viewportRef = useRef(null);
  const trackRef = useRef(null);

  const itemsPerPage = 3;
  const gap = 20;

  // measured pixel values
  const [cardWidth, setCardWidth] = useState(0);
  const [step, setStep] = useState(0);

  // fetch data
  useEffect(() => {
    fetch("http://localhost:5000/endow")
      .then((res) => {
        if (!res.ok) throw new Error("Network response not ok");
        return res.json();
      })
      .then((data) => setEndows(data || []))
      .catch((err) => console.error("Lỗi fetch endows:", err));
  }, []);

  // measure card width (px) based on computed CSS after layout/images loaded
  useEffect(() => {
    let rafId = 0;
    let timeoutId = null;

    const measure = () => {
      if (!trackRef.current) return;
      const firstCard = trackRef.current.querySelector(".endow-card");
      if (!firstCard) return;
      const rect = firstCard.getBoundingClientRect();
      const w = Math.round(rect.width);
      // if width changed, update
      if (w && w !== cardWidth) {
        setCardWidth(w);
        setStep(w + gap);
      }
    };

    const scheduleMeasure = () => {
      // use rAF then a small timeout to allow images to render
      cancelAnimationFrame(rafId);
      rafId = requestAnimationFrame(() => {
        clearTimeout(timeoutId);
        timeoutId = setTimeout(measure, 30);
      });
    };

    scheduleMeasure();
    window.addEventListener("resize", scheduleMeasure);

    // also observe image load inside track to re-measure when images finished
    let imgs = [];
    if (trackRef.current) {
      imgs = Array.from(trackRef.current.querySelectorAll("img"));
      imgs.forEach((img) => {
        if (!img.complete) img.addEventListener("load", scheduleMeasure);
      });
    }

    return () => {
      cancelAnimationFrame(rafId);
      clearTimeout(timeoutId);
      window.removeEventListener("resize", scheduleMeasure);
      imgs.forEach((img) => img.removeEventListener("load", scheduleMeasure));
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [endows]); // rerun when data changes

  // initialize offsetIndex after we have step and data
  useEffect(() => {
    if (!endows.length || step === 0) return;
    setIsAnimating(false);
    setOffsetIndex(endows.length); // start at middle copy
    const id = setTimeout(() => setIsAnimating(true), 20);
    return () => clearTimeout(id);
  }, [endows.length, step]);

  // extended array for infinite carousel
  const extended = [...endows, ...endows, ...endows];
  const extendedCount = extended.length;

  // navigation
  const next = () => {
    if (endows.length <= itemsPerPage) return;
    setIsAnimating(true);
    setOffsetIndex((s) => s + 1);
  };
  const prev = () => {
    if (endows.length <= itemsPerPage) return;
    setIsAnimating(true);
    setOffsetIndex((s) => s - 1);
  };

  // normalize after transition end
  const onTrackTransitionEnd = () => {
    if (!endows.length) return;
    if (offsetIndex >= endows.length * 2) {
      setIsAnimating(false);
      setOffsetIndex((s) => s - endows.length);
      setTimeout(() => setIsAnimating(true), 20);
    } else if (offsetIndex < endows.length) {
      setIsAnimating(false);
      setOffsetIndex((s) => s + endows.length);
      setTimeout(() => setIsAnimating(true), 20);
    }
  };

  // track inline style: only set width/transform when step known
  const trackStyle =
    step > 0
      ? {
          width: `${extendedCount * step}px`,
          transform: `translateX(-${offsetIndex * step}px)`,
          transition: isAnimating
            ? "transform 0.45s cubic-bezier(.22,.9,.18,1)"
            : "none",
        }
      : { transform: "none" };

  return (
    <section className="endow-section">
      <div className="endow-container">
        <div className="endow-title">
          Ưu đãi
          <br />
          đặc biệt
        </div>

        <div className="endow-slider-area">
          {endows.length > itemsPerPage && (
            <button
              className="endow-arrow left"
              onClick={prev}
              aria-label="Prev"
            >
              <FaChevronLeft />
            </button>
          )}

          <div className="endow-viewport" ref={viewportRef}>
            <div
              className="endow-track"
              ref={trackRef}
              style={trackStyle}
              onTransitionEnd={onTrackTransitionEnd}
            >
              {extended.map((item, idx) =>
                item ? (
                  <article
                    key={`${item.ID}-${idx}`}
                    className="endow-card"
                    onClick={() => (window.location.href = `/endow/${item.ID}`)}
                  >
                    <img
                      className="endow-poster"
                      alt={item.name}
                      src={require(`../../assets/images/Endow/${item.ID}.jpg`)}
                      onError={(e) => {
                        e.target.onerror = null;
                        try {
                        } catch {
                          e.target.src = "";
                        }
                      }}
                    />
                    <h3 className="endow-name">{item.name}</h3>
                  </article>
                ) : (
                  <div
                    key={`empty-${idx}`}
                    className="endow-card endow-card--empty"
                  />
                )
              )}
            </div>
          </div>

          {endows.length > itemsPerPage && (
            <button
              className="endow-arrow right"
              onClick={next}
              aria-label="Next"
            >
              <FaChevronRight />
            </button>
          )}
        </div>
      </div>
    </section>
  );
}

export default EndowList;
