import React, { useState, useEffect } from "react";
import { Carousel } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./MovieSlider.css";

import Slider1 from "../../assets/images/Slider1.jpg";
import Slider2 from "../../assets/images/Slider2.jpg";
import Slider3 from "../../assets/images/Slider3.jpg";
import Slider4 from "../../assets/images/Slider4.jpg";
import Slider5 from "../../assets/images/Slider5.jpg";

function MovieSlider() {
  const sliders = [Slider1, Slider2, Slider3, Slider4, Slider5];
  const [index, setIndex] = useState(0);

  // Auto next m·ªói 30 gi√¢y
  useEffect(() => {
    const interval = setInterval(() => {
      handleNext();
    }, 30000);
    return () => clearInterval(interval);
  }, []);

  const handleSelect = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  const handlePrev = () => {
    setIndex((prev) => (prev === 0 ? sliders.length - 1 : prev - 1));
  };

  const handleNext = () => {
    setIndex((prev) => (prev + 1) % sliders.length);
  };

  return (
    <div className="movie-slider">
      <Carousel
        activeIndex={index}
        onSelect={handleSelect}
        interval={null}
        controls={false}
        indicators={false}
        fade
      >
        {sliders.map((img, i) => (
          <Carousel.Item key={i}>
            <div className="slider-img-wrapper">
              <img
                src={img}
                alt={`Slide ${i + 1}`}
                className="d-block w-100 slider-img"
              />
              <div className="overlay"></div>
              <div className="slider-content">
                <h2 className="movie-title">TAY ANH GI·ªÆ M·ªòT V√å SAO</h2>
                <div className="btn-group">
                  <button className="btn btn-success me-2">
                    üéü MUA V√â NGAY
                  </button>
                  <button className="btn btn-outline-light">
                    ‚Ñπ TH√îNG TIN CHI TI·∫æT
                  </button>
                </div>
              </div>
            </div>
          </Carousel.Item>
        ))}
      </Carousel>

      {/* N√∫t ƒëi·ªÅu h∆∞·ªõng Prev / Next */}
      <button className="nav-btn prev" onClick={handlePrev}>
        ‚ùÆ
      </button>
      <button className="nav-btn next" onClick={handleNext}>
        ‚ùØ
      </button>

      {/* Thumbnail ch·ªçn slide */}
      <div className="thumbnail-wrapper">
        {sliders.map((img, i) => (
          <img
            key={i}
            src={img}
            alt={`thumb-${i}`}
            className={`thumbnail ${i === index ? "active" : ""}`}
            onClick={() => setIndex(i)}
          />
        ))}
      </div>
    </div>
  );
}

export default MovieSlider;
