import React, { useEffect, useState, useRef } from "react";
import "./EndowList.css";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

function EndowList() {
  const [endows, setEndows] = useState([]);
  const [offsetIndex, setOffsetIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(true);
  const viewportRef = useRef(null);

  const itemsPerPage = 3; // show 3 cards at the same time
  const gap = 20; // must match CSS gap
  const [itemWidth, setItemWidth] = useState(300);

  useEffect(() => {
    fetch("http://localhost:5000/endow")
      .then((res) => {
        if (!res.ok)
          throw new Error("Network response was not ok: " + res.status);
        return res.json();
      })
      .then((data) => setEndows(data || []))
      .catch((err) => console.error("Lỗi fetch endows:", err));
  }, []);

  // compute width for each item so exactly itemsPerPage fit in viewport (account gap)
  useEffect(() => {
    function recompute() {
      if (!viewportRef.current) return;
      const w = viewportRef.current.clientWidth;
      const computed = Math.floor(
        (w - gap * (itemsPerPage - 1)) / itemsPerPage
      );
      setItemWidth(computed > 120 ? computed : 120);
    }
    recompute();
    window.addEventListener("resize", recompute);
    return () => window.removeEventListener("resize", recompute);
  }, [itemsPerPage]);

  // initialize to middle copy when data ready
  useEffect(() => {
    if (!endows.length) return;
    setIsAnimating(false);
    setOffsetIndex(endows.length); // start at middle copy
    const id = setTimeout(() => setIsAnimating(true), 20);
    return () => clearTimeout(id);
  }, [endows.length, itemWidth]);

  // extended array for infinite loop
  const extended = [...endows, ...endows, ...endows];
  const step = itemWidth + gap;
  const extendedCount = extended.length;

  const next = () => {
    if (!endows.length) return;
    setIsAnimating(true);
    setOffsetIndex((s) => s + 1);
  };
  const prev = () => {
    if (!endows.length) return;
    setIsAnimating(true);
    setOffsetIndex((s) => s - 1);
  };

  // normalize after transition if out of middle copy
  const onTrackTransitionEnd = () => {
    if (!endows.length) return;
    if (offsetIndex >= endows.length * 2) {
      setIsAnimating(false);
      setOffsetIndex((s) => s - endows.length);
      setTimeout(() => setIsAnimating(true), 20);
    } else if (offsetIndex < endows.length) {
      setIsAnimating(false);
      setOffsetIndex((s) => s + endows.length);
      setTimeout(() => setIsAnimating(true), 20);
    }
  };

  const trackStyle = {
    width: `${extendedCount * step}px`,
    transform: `translateX(-${offsetIndex * step}px)`,
    transition: isAnimating
      ? "transform 0.45s cubic-bezier(.22,.9,.18,1)"
      : "none",
  };

  return (
    <section className="endow-section">
      <div className="endow-container">
        {/* left title */}
        <div className="endow-title">
          Ưu đãi
          <br />
          đặc biệt
        </div>

        {/* slider area */}
        <div className="endow-slider-area">
          <button className="endow-arrow left" onClick={prev} aria-label="Prev">
            <FaChevronLeft />
          </button>

          <div className="endow-viewport" ref={viewportRef}>
            <div
              className="endow-track"
              style={trackStyle}
              onTransitionEnd={onTrackTransitionEnd}
            >
              {extended.map((item, idx) =>
                item ? (
                  <article
                    key={`${item.ID}-${idx}`}
                    className="endow-card"
                    style={{
                      width: `${itemWidth}px`,
                      minWidth: `${itemWidth}px`,
                    }}
                    onClick={() => (window.location.href = `/endow/${item.ID}`)}
                  >
                    <img
                      className="endow-poster"
                      alt={item.name}
                      // keep require style consistent with other components
                      src={require(`../../assets/images/Endow/${item.ID}.jpg`)}
                      onError={(e) => {
                        e.target.onerror = null;
                      }}
                    />
                    <h3 className="endow-name">{item.name}</h3>
                  </article>
                ) : (
                  <div
                    key={`empty-${idx}`}
                    className="endow-card endow-card--empty"
                    style={{ width: itemWidth }}
                  />
                )
              )}
            </div>
          </div>

          <button
            className="endow-arrow right"
            onClick={next}
            aria-label="Next"
          >
            <FaChevronRight />
          </button>
        </div>
      </div>
    </section>
  );
}

export default EndowList;
