import React, { useEffect, useState, useRef } from "react";
import "./MovieList.css";
import {
  FaPlay,
  FaTicketAlt,
  FaChevronLeft,
  FaChevronRight,
  FaTimes,
} from "react-icons/fa";

function MovieList() {
  const [films, setFilms] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0); // index of first visible film
  const [showModal, setShowModal] = useState(false);
  const [modalUrl, setModalUrl] = useState("");
  const viewportRef = useRef(null);
  const [itemWidth, setItemWidth] = useState(220); // px fallback
  const itemsPerPage = 5;

  useEffect(() => {
    fetch("http://localhost:5000/films")
      .then((res) => res.json())
      .then((data) => setFilms(data))
      .catch((err) => console.error("L·ªói fetch films:", err));
  }, []);

  // compute itemWidth based on viewport width
  useEffect(() => {
    function recompute() {
      if (viewportRef.current) {
        const w = viewportRef.current.clientWidth;
        const computed = Math.floor(w / itemsPerPage);
        setItemWidth(computed);
      }
    }
    recompute();
    window.addEventListener("resize", recompute);
    return () => window.removeEventListener("resize", recompute);
  }, [itemsPerPage]);

  const maxIndex = Math.max(0, films.length - itemsPerPage);

  const prev = () => {
    setCurrentIndex((c) => Math.max(0, c - 1));
  };
  const next = () => {
    setCurrentIndex((c) => Math.min(maxIndex, c + 1));
  };

  const openModal = (link) => {
    setModalUrl(link);
    setShowModal(true);
  };
  const closeModal = () => {
    setShowModal(false);
    setModalUrl("");
  };

  useEffect(() => {
    const onKey = (e) => {
      if (e.key === "Escape") closeModal();
    };
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, []);

  // when user clicks a dot (dot index corresponds to film index),
  // clamp so the first visible film cannot exceed maxIndex
  const handleDotClick = (i) => {
    setCurrentIndex(Math.min(Math.max(0, i), maxIndex));
  };

  // style for track: width = itemWidth * films.length, translateX = -currentIndex * itemWidth px
  const trackStyle = {
    width: `${films.length * itemWidth}px`,
    transform: `translateX(-${currentIndex * itemWidth}px)`,
  };

  return (
    <section className="movie-list-container">
      <h2 className="movie-list-title">
        <span className="movie-list-title-inner">üé¨ PHIM ƒêANG CHI·∫æU</span>
      </h2>

      <button className="arrow left" onClick={prev} aria-label="Prev">
        <FaChevronLeft />
      </button>
      <button className="arrow right" onClick={next} aria-label="Next">
        <FaChevronRight />
      </button>

      <div className="movie-slider-viewport" ref={viewportRef}>
        <div className="movie-track" style={trackStyle}>
          {films.map((film) => (
            <article
              key={film.ID}
              className="movie-card"
              style={{ width: `${itemWidth}px`, minWidth: `${itemWidth}px` }}
            >
              <div
                className="poster-wrapper"
                onClick={() => (window.location.href = `/film/${film.ID}`)}
              >
                <img
                  className="movie-poster"
                  alt={film.name}
                  src={require(`../../assets/images/Film/${film.ID}.jpg`)}
                />
                <div className="card-overlay" />
                <div className="overlay-buttons">
                  {film.link && (
                    <button
                      className="btn play"
                      onClick={(e) => {
                        e.stopPropagation();
                        openModal(film.link);
                      }}
                    >
                      <FaPlay />
                    </button>
                  )}
                  <button
                    className="btn buy"
                    onClick={(e) => {
                      e.stopPropagation();
                      window.location.href = `/film/${film.ID}/buy`;
                    }}
                  >
                    <FaTicketAlt />
                    <span className="buy-text">MUA V√â NGAY</span>
                  </button>
                </div>
              </div>

              <h3 className="movie-name" title={film.name}>
                {film.name}
              </h3>
              <p className="movie-type">Th·ªÉ lo·∫°i: {film.type}</p>
            </article>
          ))}

          {/* preserve layout: if fewer items than itemsPerPage we may still keep track width ok */}
        </div>
      </div>

      <div className="carousel-dots">
        {films.map((_, idx) => (
          <button
            key={idx}
            className={`dot ${idx === currentIndex ? "active" : ""}`}
            onClick={() => handleDotClick(idx)}
            aria-label={`Go to ${idx + 1}`}
          />
        ))}
      </div>

      {showModal && (
        <div
          className="video-modal-overlay"
          onMouseDown={(e) => {
            if (e.target.classList.contains("video-modal-overlay"))
              closeModal();
          }}
        >
          <div className="video-modal">
            <button
              className="modal-close"
              onClick={closeModal}
              aria-label="Close"
            >
              <FaTimes />
            </button>
            <div className="video-wrapper">
              {modalUrl.includes("youtube.com") ||
              modalUrl.includes("youtu.be") ? (
                <iframe
                  title="Trailer"
                  src={convertYoutubeToEmbed(modalUrl)}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                />
              ) : (
                <iframe
                  title="Trailer"
                  src={modalUrl}
                  frameBorder="0"
                  allowFullScreen
                />
              )}
            </div>
          </div>
        </div>
      )}
    </section>
  );
}

function convertYoutubeToEmbed(url) {
  try {
    const u = new URL(url);
    let id = "";
    if (u.hostname.includes("youtu.be")) {
      id = u.pathname.slice(1);
    } else {
      id = u.searchParams.get("v");
    }
    if (!id) return url;
    return `https://www.youtube.com/embed/${id}?rel=0&autoplay=1`;
  } catch {
    return url;
  }
}

export default MovieList;
