import React, { useEffect, useState, useRef } from "react";
import "./EndowList.css";
import { ChevronLeft, ChevronRight } from "lucide-react";

function EndowList() {
  const [endows, setEndows] = useState([]);
  const scrollRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:5000/endow")
      .then((res) => res.json())
      .then((data) => setEndows(data))
      .catch((err) => console.error("Lỗi fetch endows:", err));
  }, []);

  const scrollLeft = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: -300, behavior: "smooth" });
    }
  };

  const scrollRight = () => {
    if (scrollRef.current) {
      scrollRef.current.scrollBy({ left: 300, behavior: "smooth" });
    }
  };

  return (
    <div className="endow-container">
      {/* ✅ Title bên trái */}
      <div className="endow-title">
        Ưu đãi <br /> đặc biệt
      </div>

      {/* ✅ Slider */}
      <div className="endow-slider-wrapper">
        <button className="endow-nav left" onClick={scrollLeft}>
          <ChevronLeft size={28} />
        </button>

        <div className="endow-list" ref={scrollRef}>
          {endows.map((item) => (
            <div key={item.ID} className="endow-card">
              <img
                src={require(`../../assets/images/Endow/${item.ID}.jpg`)}
                alt={item.name}
              />
              <p className="endow-name">{item.name}</p>
            </div>
          ))}
        </div>

        <button className="endow-nav right" onClick={scrollRight}>
          <ChevronRight size={28} />
        </button>
      </div>
    </div>
  );
}

export default EndowList;
