import React, { useEffect, useState, useRef } from "react";
import "./EndowList.css";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

function EndowList() {
  const [endows, setEndows] = useState([]);
  const [index, setIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const itemsPerPage = 3;

  const trackRef = useRef(null);

  // fetch data
  useEffect(() => {
    fetch("http://localhost:5000/endow")
      .then((res) => {
        if (!res.ok) throw new Error("Network error");
        return res.json();
      })
      .then((data) => {
        setEndows(data || []);
        setIndex(itemsPerPage); // luôn bắt đầu ở "real first"
      })
      .catch((err) => console.error("Lỗi fetch endows:", err));
  }, []);

  // clone đầu/cuối để tạo loop
  const extendedEndows = [
    ...endows.slice(-itemsPerPage),
    ...endows,
    ...endows.slice(0, itemsPerPage),
  ];

  // --- 🔥 update phần next/prev ---
  const handleNext = () => {
    if (isTransitioning || endows.length === 0) return;
    setIsTransitioning(true);
    setIndex((prev) => prev + 1);
  };

  const handlePrev = () => {
    if (isTransitioning || endows.length === 0) return;
    setIsTransitioning(true);
    setIndex((prev) => prev - 1);
  };

  const handleTransitionEnd = () => {
    setIsTransitioning(false);

    // reset vòng lặp
    if (index >= endows.length + itemsPerPage) {
      setIndex(itemsPerPage); // về đầu thực
    } else if (index < itemsPerPage) {
      setIndex(endows.length + itemsPerPage - 1); // về cuối thực
    }
  };

  // offset tính theo số item (1 bước = 100/itemsPerPage %)
  const offsetPercent = (100 / itemsPerPage) * index;

  return (
    <section className="endow-section">
      <div className="endow-container">
        <div className="endow-title">
          Ưu đãi
          <br />
          đặc biệt
        </div>

        <div className="endow-slider-area">
          <button className="endow-arrow left" onClick={handlePrev}>
            <FaChevronLeft />
          </button>

          <div className="endow-viewport">
            <div
              ref={trackRef}
              className="endow-track"
              style={{
                transform: `translateX(-${offsetPercent}%)`,
                transition: isTransitioning ? "transform 0.45s ease" : "none",
              }}
              onTransitionEnd={handleTransitionEnd}
            >
              {extendedEndows.map((item, idx) => (
                <article
                  key={`${item.ID}-${idx}`}
                  className="endow-card"
                  onClick={() => (window.location.href = `/endow/${item.ID}`)}
                >
                  <img
                    className="endow-poster"
                    alt={item.name}
                    src={require(`../../assets/images/Endow/${item.ID}.jpg`)}
                    onError={(e) => {
                      try {
                      } catch {
                        e.target.src = "";
                      }
                    }}
                  />
                  <h3 className="endow-name">{item.name}</h3>
                </article>
              ))}
            </div>
          </div>

          <button className="endow-arrow right" onClick={handleNext}>
            <FaChevronRight />
          </button>
        </div>
      </div>
    </section>
  );
}

export default EndowList;
