import React, { useEffect, useState } from "react";
import "./MovieList.css";
import {
  FaPlay,
  FaTicketAlt,
  FaChevronLeft,
  FaChevronRight,
} from "react-icons/fa";

function MovieList() {
  const [films, setFilms] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    fetch("http://localhost:5000/films")
      .then((res) => res.json())
      .then((data) => setFilms(data))
      .catch((err) => console.error("L·ªói fetch films:", err));
  }, []);

  const handleFilmClick = (id) => {
    window.location.href = `/film/${id}`;
  };

  const itemsPerPage = 5;
  const totalPages = Math.ceil(films.length / itemsPerPage);

  const prevPage = () => {
    setCurrentIndex((prev) => (prev > 0 ? prev - 1 : totalPages - 1));
  };

  const nextPage = () => {
    setCurrentIndex((prev) => (prev < totalPages - 1 ? prev + 1 : 0));
  };

  return (
    <div className="movie-list-container">
      <h2 className="movie-list-title">üé¨ PHIM ƒêANG CHI·∫æU</h2>

      {/* Arrow buttons */}
      <button className="arrow left" onClick={prevPage}>
        <FaChevronLeft />
      </button>
      <button className="arrow right" onClick={nextPage}>
        <FaChevronRight />
      </button>

      <div className="movie-cards">
        {films
          .slice(
            currentIndex * itemsPerPage,
            currentIndex * itemsPerPage + itemsPerPage
          )
          .map((film) => (
            <div key={film.ID} className="movie-card">
              <div
                className="poster-wrapper"
                onClick={() => handleFilmClick(film.ID)}
              >
                <img
                  src={require(`../../assets/images/Film/${film.ID}.jpg`)}
                  alt={film.name}
                  className="movie-poster"
                />
                <div className="overlay-buttons">
                  {film.link && (
                    <button
                      className="btn play"
                      onClick={(e) => {
                        e.stopPropagation();
                        window.open(film.link, "_blank");
                      }}
                    >
                      <FaPlay />
                    </button>
                  )}
                  <button className="btn buy">
                    <FaTicketAlt /> MUA V√â NGAY
                  </button>
                </div>
              </div>
              <h3 className="movie-name">{film.name}</h3>
              <p className="movie-type">Th·ªÉ lo·∫°i: {film.type}</p>
            </div>
          ))}
      </div>

      <div className="carousel-dots">
        {Array.from({ length: totalPages }).map((_, index) => (
          <span
            key={index}
            className={`dot ${index === currentIndex ? "active" : ""}`}
            onClick={() => setCurrentIndex(index)}
          ></span>
        ))}
      </div>
    </div>
  );
}

export default MovieList;
