import React, { useEffect, useState, useRef } from "react";
import "./EndowList.css";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

function EndowList() {
  const [endows, setEndows] = useState([]);
  const [offsetIndex, setOffsetIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(true);
  const viewportRef = useRef(null);

  const itemsPerPage = 3;
  const gap = 20;
  const [itemWidth, setItemWidth] = useState(260);

  useEffect(() => {
    fetch("http://localhost:5000/endow")
      .then((res) => res.json())
      .then((data) => setEndows(data || []))
      .catch((err) => console.error("❌ Lỗi fetch endows:", err));
  }, []);

  useEffect(() => {
    function recompute() {
      if (!viewportRef.current) return;
      const w = viewportRef.current.clientWidth;
      const computed = Math.floor(
        (w - gap * (itemsPerPage - 1)) / itemsPerPage
      );
      setItemWidth(computed > 160 ? computed : 160);
    }
    recompute();
    window.addEventListener("resize", recompute);
    return () => window.removeEventListener("resize", recompute);
  }, [itemsPerPage]);

  useEffect(() => {
    if (endows.length === 0) return;
    setIsAnimating(false);
    setOffsetIndex(endows.length);
    const id = setTimeout(() => setIsAnimating(true), 20);
    return () => clearTimeout(id);
  }, [endows.length, itemWidth]);

  const extended = [...endows, ...endows, ...endows];
  const step = itemWidth + gap;

  const next = () => {
    if (endows.length === 0) return;
    setIsAnimating(true);
    setOffsetIndex((s) => s + 1);
  };
  const prev = () => {
    if (endows.length === 0) return;
    setIsAnimating(true);
    setOffsetIndex((s) => s - 1);
  };

  const onTrackTransitionEnd = () => {
    if (endows.length === 0) return;
    if (offsetIndex >= endows.length * 2) {
      setIsAnimating(false);
      setOffsetIndex((s) => s - endows.length);
      setTimeout(() => setIsAnimating(true), 20);
    } else if (offsetIndex < endows.length) {
      setIsAnimating(false);
      setOffsetIndex((s) => s + endows.length);
      setTimeout(() => setIsAnimating(true), 20);
    }
  };

  const activeDot = endows.length
    ? ((offsetIndex % endows.length) + endows.length) % endows.length
    : 0;

  const trackStyle = {
    width: `${extended.length * step}px`,
    transform: `translateX(-${offsetIndex * step}px)`,
    transition: isAnimating
      ? "transform 0.45s cubic-bezier(.22,.9,.18,1)"
      : "none",
  };

  return (
    <section className="endow-list-container">
      <h2 className="endow-list-title">
        <span className="endow-list-title-inner">Ưu đãi đặc biệt</span>
      </h2>

      <button className="arrow left" onClick={prev}>
        <FaChevronLeft />
      </button>
      <button className="arrow right" onClick={next}>
        <FaChevronRight />
      </button>

      <div className="endow-slider-viewport" ref={viewportRef}>
        <div
          className="endow-track"
          style={trackStyle}
          onTransitionEnd={onTrackTransitionEnd}
        >
          {extended.map((endow, idx) =>
            endow ? (
              <article
                key={`${endow.ID}-${idx}`}
                className="endow-card"
                style={{ width: `${itemWidth}px`, minWidth: `${itemWidth}px` }}
              >
                <img
                  className="endow-poster"
                  alt={endow.name}
                  src={require(`../../assets/images/Endow/${endow.ID}.jpg`)}
                />
                <h3 className="endow-name">{endow.name}</h3>
              </article>
            ) : (
              <div
                key={`empty-${idx}`}
                className="endow-card endow-card--empty"
                style={{ width: itemWidth }}
              />
            )
          )}
        </div>
      </div>

      <div className="carousel-dots">
        {endows.map((_, idx) => (
          <button
            key={idx}
            className={`dot ${idx === activeDot ? "active" : ""}`}
            onClick={() => setOffsetIndex(endows.length + idx)}
          />
        ))}
      </div>
    </section>
  );
}

export default EndowList;
